'use strict';

var rtStyle = require('../../src/rtStyle');

module.exports = {
    runTests: function runTests(test) {
        test('html tests', function (t) {
            var text = '.text { background-color: #00346E; padding: 3px; }';
            var expected = '{\n  "text": {\n    "backgroundColor": "#00346E",\n    "padding": 3\n  }\n}';
            var actual = rtStyle.convertBody(text);
            t.equal(actual, expected);
            t.end();
        });
    }
};