'use strict';

var test = require('tape');
var path = require('path');
var dataPath = path.resolve(__dirname, '..', 'data');

var specs = ['rt.invalid', 'rt.valid', 'utils', 'shell', 'rtStyle', 'fsUtil'];

specs.map(function (file) {
    return require('./' + file + '.spec');
}).forEach(function (spec) {
    return spec.runTests(test, dataPath);
});