'use strict';

var utils = require('../../src/utils');

module.exports = {
    runTests: function runTests(test) {
        test('test convertText', function (t) {
            var texts = [{ input: '{}', expected: '()' }, { input: "a {'b'}", expected: '"a "+(\'b\')' }];
            t.plan(texts.length);
            texts.forEach(check);
            function check(testData) {
                var r = utils.convertText({}, {}, testData.input);
                t.equal(r, testData.expected);
            }
        });
    }
};